import React, { Fragment } from "react";
import CampImage from "./part/CampImage";
import CampCategory from "./part/CampCategory";
import CampTitle from "./part/CampTitle";
import CampDesc from "./part/CampDesc";
import CampMeta from "./part/CampMeta";
import { Button } from "components/button";

const CampaignView = () => {
  return (
    <Fragment>
      <div
        className="h-[140px] rounded-3xl bg-cover bg-no-repeat bg-center bg-opacity-40 flex items-center justify-center text-white text-[40px] font-bold mb-10"
        style={{
          backgroundImage: `url(https://source.unsplash.com/random)`,
        }}
      >
        <h1>Education</h1>
      </div>
      <div className="flex items-center w-full gap-x-10 max-w-[1066px]">
      <div className="flex-1">
      <CampImage className="h-[398px]"></CampImage>

      </div>
      <div className="flex-1 max-w-[435px]">
        <CampCategory
          text="Architecture"
          className="mb-4 text-sm"
        ></CampCategory>
        <CampTitle className="mb-4 text-xl font-bold">
          {" "}
          Remake - We Make architecture exhibition
        </CampTitle>
        <CampDesc className="mb-4 text-sm">
          Remake - We Make: an exhibition about architecture's social agency in
          the face of urbanisation
        </CampDesc>
        <div className="w-full rounded-full bg-[#EFEFEF] h-[5px] mb-6">
            <div className="w-2/4 h-full rounded-full bg-primary"></div>
        </div>
        <div className="flex items-start justify-between mb-4 gap-x-5">
            <CampMeta size="big"></CampMeta>
            <CampMeta size="big"></CampMeta>
            <CampMeta size="big"></CampMeta>

        </div>
        <Button className=""></Button>
      </div>
    </div>
    </Fragment>
  );
};

export default CampaignView;
