import React from "react";
import PropTypes from "prop-types";

const Button = ({
  type = "button",
  children,
  className = "",
  isLoading = false,
  ...rest
}) => {
    const child = !!isLoading ? <div className="w-10 h-10 border-4 border-white rounded-full border-top-transparent animate-spin" ></div> : children;
  return (
    <button
      type={type}
      className={`flex items-center justify-center p-4 text-base font-semibold rounded-xl min-h-[56px] text-white ${className}`}
      {...rest}
    >
      {child}
    </button>
  );
};

Button.propTypes = {
  type: PropTypes.string.isRequired,
  children: PropTypes.node,
  children
};
export default Button;
